  deleteRepetionChar(arr){
    var arrTable = {},arrData = [];
    for (var i = 0; i < arr.length; i++) {
      if( !arrTable[ arr[i] ]){
        arrTable[ arr[i] ] = true;
        arrData.push(arr[i])
      }
    }
    return arrData;
  },
